{
  "modelReport": {
    "title": "Typhoon Impact Model",
    "initiative": "510 - An initiative of the Netherlands Red Cross",
    "date": "August 2022",
    "background": {
      "description": "This document summarises the documentation and the findings of the peer review of the trigger used in OCHA’s Philippines Anticipatory Action framework for typhoons.",
      "modelDeveloper": "Netherlands Red Cross 510 data and digital team",
      "modelPurpose": "To predict the percentage of houses that will be severely damaged per municipality in the Philippines"
    },
    "mainFindingsAndRecommendations": {
      "technicalReview": {
        "intendedUse": {
          "description": "The target of the model is P (#damaged houses > threshold t)",
          "recommendation": "Make a clear statement under which circumstance which combination is used"
        },
        "modelDevelopment": {
          "description": "More information about the sources of the input data is desirable",
          "recommendation": "Include information on how often the data sets are updated, and how often the model is retrained to reflect these updates"
        },
        "modelEvaluation": {
          "description": "The model is compared against a damage curve that is currently used to estimate damage",
          "recommendation": "Adjust this benchmark to whichever model or algorithm is used by the Philippine authorities to estimate damage"
        },
        "operationalReadiness": {
          "description": "For every model version, make a model release and keep an updated list of associated input data",
          "recommendation": "This will make reproducibility of earlier results easier and help keep track of the data sources"
        }
      },
      "ethicalReview": {
        "inaccuracy": {
          "description": "The model performance scores for 2021 Typhoon Surigae are not reported",
          "recommendation": "Report latest error scores, contextualise the quality of the error scores, and include the rationale for using a particular method of calculating error score"
        },
        "falseNegatives": {
          "description": "In the case of Typhoon Goni in 2020, the model did not predict an extreme event until only a few hours before landfall",
          "recommendation": "If the model does have 100% weightage in the activation of the trigger, then the possibility of a false negative should be clear operationally"
        },
        "missingAttributes": {
          "description": "The implementation model mentions a priority list based on poverty, gender, and building material, while the model uses poverty, hazard data, and building type",
          "recommendation": "Clarity is required on the relative importance of variables used, and consistency between the model and the implementation plan"
        },
        "leadTimes": {
          "description": "Lead time is the length of time between when a forecast is output and the occurrence of the shock that was forecasted",
          "recommendation": "In the implementation plan, more clarity is required on potential operational impact of long lead times"
        },
        "decisionSupport": {
          "description": "Decision support concerns the extent to which action based on model output",
          "recommendation": "Clarity is required in the implementation plan about weightage of model output on trigger activation"
        },
        "statisticalBias": {
          "description": "Statistical bias occurs when a model or statistic is not representative of the underlying population",
          "recommendation": "The model requires clear documentation of the different datasets used, particularly the rationale behind parameters used and their disaggregation"
        },
        "lackOfTransparency": {
          "description": "The model is open source and has undergone technical review but is missing some documentation",
          "recommendation": "The model would benefit from better documentation of the modelling process, rationale for the algorithm selection, its fitness for purpose, inclusion/exclusion criteria for variables, etc."
        },
        "systemicBias": {
          "description": "Systemic bias is the inherent tendency of a process to support specific outcomes",
          "recommendation": "Clarity, rationale and disaggregation of parameters like social vulnerability and poverty would be beneficial"
        }
      }
    }
  }
}

