{"regulations": {
  "section": "214",
  "subsections": {
    "214.3": {
      "title": "Assumptions",
      "description": "Presidential emergency authority, including Executive Orders, will be in effect during an attack or post-attack period"
    },
    "214.4": {
      "title": "Planned actions",
      "actions": [
        {
          "action": "Exercise emergency authority",
          "description": "Director, OSTP, will exercise authority as specified in Executive Order 12472"
        },
        {
          "action": "Authorize frequency authorizations",
          "description": "Continuance of all frequency authorizations issued by NTIA and FCC, except as modified or revoked by Director, OSTP"
        },
        {
          "action": "Redelegate authority",
          "description": "Redelegate authority to Secretary of Defense to control radio spectrum in areas of active combat"
        },
        {
          "action": "Close non-government radio stations",
          "description": "Close all non-government radio stations in international broadcasting service, except those carrying US Government-controlled radio broadcasts"
        }
      ]
    },
    "214.5": {
      "title": "Responsibilities",
      "responsibilities": [
        {
          "entity": "Director, OSTP",
          "description": "Issue policy guidance, rules, regulations, procedures, and directives for effective frequency usage during wartime emergency conditions"
        },
        {
          "entity": "FCC",
          "description": "Issue rules, regulations, orders, and instructions to ensure effective use of radio spectrum shared by government and non-government users"
        },
        {
          "entity": "Federal Government agencies",
          "description": "Develop and implement own plans, and make necessary pre-emergency arrangements with non-government entities"
        }
      ]
    },
    "214.6": {
      "title": "Postattack procedures and actions",
      "procedures": [
        {
          "procedure": "Frequency management staff relocation",
          "description": "Frequency management staff will proceed to OSTP relocation site in accordance with alerting orders"
        },
        {
          "procedure": "Application submission",
          "description": "Government agencies submit applications for new or modified radio frequency assignments to Director, OSTP"
        },
        {
          "procedure": "Non-government entity application submission",
          "description": "Non-government entities submit applications for new or modified radio frequency assignments to FCC"
        }
      ]
    }
  }
}}

