{
  "title": "Somalia Flood Exposure Methodology Note",
  "analysis": "2024 HNRP",
  "methodology": {
    "dataSources": [
      "Daily FloodScan (1998-2022)",
      "WorldPop (2020 UN Adjusted)"
    ],
    "seasons": [
      "March-April-May (MAM)",
      "October-November-December (OND)"
    ],
    "processingSteps": [
      {
        "step": 1,
        "description": "Composites reclassified to binary using a 20 percent flood fraction threshold"
      },
      {
        "step": 2,
        "description": "Composites masked to just remove any flood fraction values < 0.05 percent and the flood fraction (0-100 percent) retained"
      }
    ],
    "output": {
      "rasterType": "yearly seasonal flood exposure rasters",
      "aggregationLevel": "second administrative level",
      "statistics": "zonal statistics (sum)"
    }
  },
  "exposureEstimates": {
    "ranges": {
      "MAM": {
        "percentileRange": "50th-95th",
        "reason": "ECMWF seasonal forecast predicts an above average precipitation season for MAM 2024"
      },
      "OND": {
        "percentileRange": "25-75th",
        "reason": "No available data to inform predictions for OND 2024"
      }
    },
    "aggregation": {
      "level": "administrative level 1",
      "method": "conservative combination of range estimates"
    }
  },
  "contact": {
    "name": "Leonardo Milano",
    "title": "Team Lead for Data Science",
    "email": "leonardo.milano@un.org"
  }
}

{
  "centre_for_humanitarian_data": {
    "guidance_series": "The climate crisis is intensifying humanitarian emergencies around the world and humanitarians are increasingly having to incorporate climate data and forecasts into their analysis and planning.",
    "observational_rainfall_data": {
      "definition": "Captures how much rain fell over a defined period of time",
      "types": [
        "station data",
        "satellite or radar imagery"
      ],
      "advantages": {
        "station_data": [
          "accurate at specific locations",
          "collected frequently",
          "long-term collection allows for trend analysis"
        ],
        "satellite_data": [
          "extensive geographic coverage",
          "shared in a continuous and consistent way"
        ]
      },
      "disadvantages": {
        "station_data": [
          "only available for specific locations",
          "subject to bias due to wind, evaporation, and changes in measurement devices"
        ],
        "satellite_data": [
          "subject to errors due to cloud contamination",
          "shorter historical record"
        ]
      }
    },
    "common_sources": [
      {
        "name": "CHIRPS",
        "description": "Climate Hazards Group InfraRed Precipitation with Station data",
        "provider": "US Geological Survey and the Climate Hazards Center at the University of Santa Barbara",
        "format": "Raster",
        "geographical_coverage": "Global (quasi)",
        "spatial_granularity": "0.05 degree (5.5 km at the equator)",
        "temporal_granularity": "Daily rainfall",
        "historical_coverage": "1981 - today",
        "publication": "Every month, around the third week of the following month"
      },
      {
        "name": "ARC2",
        "description": "Africa Rainfall Climatology Version 2",
        "provider": "The National Oceanic and Atmospheric Administration’s Climate Prediction Center for the Famine Early Warning System",
        "format": "Raster",
        "geographical_coverage": "Africa",
        "spatial_granularity": "0.1 degree (11 km at the equator)",
        "temporal_granularity": "Daily rainfall",
        "historical_coverage": "1983 - today",
        "publication": "Every day, with a 2-day lag"
      }
    ],
    "questions_to_answer": [
      "How much rain fell in the past x days/weeks/months?",
      "Was the rainfall last month above or below normal? And by how much?",
      "Were there more rainy days this month than last month?",
      "How many dry spells were there during the past rainy season? How long was the longest dry spell and the heaviest rain streak this past rainy season?",
      "How often in the past decade were more than x millimeters of rainfall measured?",
      "Which region received the most rain last month?"
    ],
    "common_limitations": [
      "Low-amount or short-duration rainfall may be underreported in certain geographical terrains or during extreme precipitation events",
      "Fewer gauge stations in rural areas and certain developing countries, resulting in less accurate observational rainfall data",
      "Observational rainfall data is usually provided as a raster dataset, which can lead to errors when aggregating data for administrative areas",
      "Station data is subject to deterioration and requires frequent maintenance, which may affect its accuracy",
      "Differences exist between measuring instruments and quality of their measurements, which can heavily affect comparisons between different locations"
    ],
    "resources": [
      {
        "name": "CHIRPS",
        "url": "https://www.chc.ucsb.edu/data/chirps"
      },
      {
        "name": "ARC2",
        "url": "https://www.icpac.net/data-center/arc2/"
      },
      {
        "name": "CHIRPS on HDX",
        "url": "https://data.humdata.org/dataset?q=CHIRPS"
      },
      {
        "name": "OCHA AnticiPy",
        "url": "https://github.com/OCHA-DAP/ocha-anticipy"
      }
    ]
  }
}

